-- Datos mock para tabla ciudadanos
CREATE TABLE IF NOT EXISTS ciudadanos (
    tipo_doc VARCHAR(4) NOT NULL,
    nro_doc VARCHAR(8) NOT NULL,
    nombre1 VARCHAR(80) NOT NULL,
    nombre2 VARCHAR(80),
    apellido1 VARCHAR(80) NOT NULL,
    apellido2 VARCHAR(80),
    sexo INTEGER NOT NULL,
    fecha_nac DATE NOT NULL,
    cod_nacionalidad INTEGER NOT NULL,
    nombre_en_cedula VARCHAR(200) NOT NULL,
    PRIMARY KEY (tipo_doc, nro_doc)
);

